INSERT INTO swn_faction_goal (name, description) VALUES ('Commercial Expansion','Destroy a number of Wealth assets of rival factions equal to your faction’s Wealth rating. Difficulty is 1/2 number of assets destroyed, rounded up.');
INSERT INTO swn_faction_goal (name, description) VALUES ('Intelligence Coup','Destroy a number of Cunning assets of rival factions equal to your faction’s Cunning rating. Difficulty is 1/2 number of assets destroyed, rounded up.');
INSERT INTO swn_faction_goal (name, description) VALUES ('Planetary Seizure','Take control of a planet, becoming the legitimate planetary government. Difficulty equal to half the average of the current ruling faction’s Force, Cunning, and Wealth ratings. If the planet somehow lacks any opposing faction to resist the seizure, it counts as Difficulty 1');
INSERT INTO swn_faction_goal (name, description) VALUES ('Expand Influence','Plant a Base of Influence on a new planet. Difficulty 1, +1 if the attempt is contested by a rival faction. Difficulty 2.');
INSERT INTO swn_faction_goal (name, description) VALUES ('Blood the Enemy','Inflict a number of hit points of damage on an enemy faction equal to your faction’s total Force, Cunning, and Wealth ratings.');
INSERT INTO swn_faction_goal (name, description) VALUES ('Peaceable Kingdom','Launch no attacks on rival factions for four turns. The faction may still defend. Difficulty 1.');
INSERT INTO swn_faction_goal (name, description) VALUES ('Destroy the Foe','Destroy a rival faction. Difficulty equal to 1 plus theaverage of the faction’s Force, Cunning, and Wealth ratings.');
INSERT INTO swn_faction_goal (name, description) VALUES ('Inside Enemy Territory','Plant a number of stealthed assets on worlds with other planetary governments equal to your Cunning score. Units that are already stealthed on worlds when this goal is adopted don’t count. Difficulty 2.');
INSERT INTO swn_faction_goal (name, description) VALUES ('Invincible Valor','Destroy a Force asset with a minimum purchase rating higher than your faction’s Force rating. Thus, if your Force is 3, you need to destroy a unit that requires Force 4 or higher to purchase. Difficulty 2.');
INSERT INTO swn_faction_goal (name, description) VALUES ('Wealth of Worlds','Spend FacCreds equal to four times your faction’s Wealth rating on bribes and influence. This money is effectively lost, but the goal is then considered accomplished. The faction’s Wealth rating must increase before this goal can be selected again. Difficulty2.');
