INSERT INTO "swn_quickheresyfounder" VALUES (1, 'Defrocked clergy: founded by a cleric outcast from the faith.');
INSERT INTO "swn_quickheresyfounder" VALUES (2, 'Frustrated layman: founded by a layman frustrated with the faith''s decadence, rigidity, or lack of authenticity.');
INSERT INTO "swn_quickheresyfounder" VALUES (3, 'Renegade prophet: founded by a revered holy figure who broke with the faith.');
INSERT INTO "swn_quickheresyfounder" VALUES (4, 'High prelate: founded by an important and powerful cleric to convey his or her beliefs.');
INSERT INTO "swn_quickheresyfounder" VALUES (5, 'Dissatisfied minor clergy: founded by a minor cleric frustrated with the faith''s current condition.');
INSERT INTO "swn_quickheresyfounder" VALUES (6, 'Outsider: founded by a member of another faith deeply influenced by the parent religion.');
INSERT INTO "swn_quickheresyfounder" VALUES (7, 'Academic: founded by a professor or theologian on intellectual grounds.');
INSERT INTO "swn_quickheresyfounder" VALUES (8, 'Accidental; the founder never meant their works to be taken that way.');
